+/*** Start Home Banner ***/
+.home-banner{
+    position: relative;
+    overflow: hidden;
+  }
+  
+  .home-banner .animated {
+    animation-delay: 1.5s;
+  }
+  
+  .home-banner__banner,
+  .home-banner__container {
+    min-height: 400px;
+  }
+  
+  .home-banner__banner {
+    background-image: url('../images/home/banner-mobile.jpg');
+    background-size: cover;
+    background-position: center;
+    overflow: hidden;
+  }
+  
+  .home-banner__banner:before {
+    content: '';
+    display: block;
+    position: absolute;
+    top: 0;
+    bottom: 0;
+    left: 0;
+    right: 0;
+    background: linear-gradient(rgba(0, 0, 0, 0.35), rgba(0, 0, 0, 0.35));
+  }
+  
+  .home-banner__stroke-container{
+    position: absolute;
+    top: 50%;
+    left: 50%;
+    transform: translate(-50%, -50%);
+  }
+  
+  .home-banner__stroke{
+    height: 123px;
+    width: 138px;
+    box-shadow: 0px 0px 0px 2000px #000;
+    opacity: 0.5;
+    transform: scale3d(1.02, 1.02, 1.02);
+    animation: scale-effect 1s forwards 1s;
+    background: linear-gradient(128deg, #000 78px, transparent 78px, transparent 110px, #000 110px, #000);
+  }
+  
+  .home-banner-text{
+    color: #ffffff;
+    position: relative;
+    z-index: 0;
+    margin-top: 85px;
+  }
+  
+  .home-banner__title{
+    font-size: 26px;
+    font-family: "Publico-Headline", serif;
+    font-weight: bold;
+    margin-bottom: 10px;
+    opacity: 0;
+    animation: fade-left-effect 1s forwards 1.5s;
+  }
+  
+  .home-banner__sub-title{
+    font-size: 18px;
+    line-height: 1.11;
+    opacity: 0;
+    animation: fade-left-effect 1s forwards 1.7s;
+  }
+  
+  .home-banner__footer{
+    position: absolute;
+    bottom:0px;
+    width:100%;
+    height:130px;
+    background-image: linear-gradient(to top, #00005b, rgba(0, 0, 0, 0));  
+    text-align:center;
+    padding-top:30px;
+  }
+  .home-banner__footer .link{
+    display:inline-block;
+    position: relative;
+    width:150px;
+    height:70px;
+    border-radius: 8px;
+    box-shadow: 4px 4px 6px 0 rgba(0, 0, 0, 0.16);
+    margin:0 5px;
+    padding:10px;
+    text-align:left;
+    color: #ffffff;
+    transition: 0.2s all ease-in;
+    top:0;
+    vertical-align: top;
+  }
+  
+  .home-banner__footer .link:hover{
+    text-decoration:none;
+    box-shadow: 2px 2px 6px 0 rgba(0, 0, 0, 0.8);
+    top: -5px;
+  }
+  
+  .home-banner__footer .home-card-title{
+    font-family: "Publico-Headline", serif;
+    font-size: 14px;
+    line-height: 1.29em;
+    letter-spacing: 0.35px;
+  }
+  .home-banner__footer .home-card-content{
+    font-size: 12px;
+    line-height: 1;
+  }
+  .home-banner__footer .home-card-click{
+    font-size: 12px;
+    position: absolute;
+    bottom:5px;
+    right:5px;
+  }
+  
+  .home-banner__footer .home-card-click .icon{
+    font-size: 6px;
+    top: 1px;
+    display: inline-block;
+    position: relative;
+  }
+  
+  .home-banner__footer .link.card-honor{
+    background-image: linear-gradient(to right, #bc9d45, #655119);
+  }
+  
+  .home-banner__footer .link.card-premier{
+    background-image: linear-gradient(to right, #a8a8a8, #292929);
+  }
+  /*** End Home Banner ***/
+  /*** Start Home Category ***/
+  .home-category {
+    height: 90px;
+    background-color: #00005b;
+  }
+  
+  .category-item {
+    position:relative;
+    margin-top:20px;
+  }
+  .category-item a{
+    color: #ffffff;
+    font-size: 16px;
+  }
+  .category-item+.category-item:before{
+    content: '';
+    display: block;
+    position: absolute;
+    width:1px;
